package com.gu.mobilepurchases.validate

import com.gu.mobilepurchases.model.{ ValidatedTransaction, ValidatedTransactionPurchase, ValidatedTransactionPurchaseActiveInterval }

import scala.io.Source

object ValidateExample {
  def successExample: ValidateExample = ValidateExample("success")

  def apply(folderName: String): ValidateExample = ValidateExample(Source.fromResource(s"validate/example/$folderName/request.json").mkString, Source.fromResource(s"validate/example/$folderName/response.json").mkString)

  def successValidateRequest = ValidateRequest(
    ValidateRequestUserIds("gia:319B18F0-3B3A-40FD-9086-6DED1F566D2A", "5E1CFD76-48C7-40F8-8574-D7A7F25D9943"),
    Map("systemName" -> "iOS", "systemVersion" -> "11.2.6"),
    ValidateRequestAppInfo("uk.co.guardian.iphone2"), List(
      successValidateRequestTransaction),
    "guardian")

  def successValidateRequestTransaction: ValidateRequestTransaction = ValidateRequestTransaction(
    "20000034829192",
    "",
    "purchased")

  def successProdValidatedTransaction: ValidatedTransaction = ValidatedTransaction(
    "20000034829192",
    validated = true,
    finishTransaction = true,
    Some(ValidatedTransactionPurchase("uk.co.guardian.gce.plusobserver.1monthsub", "20000001746150", ValidatedTransactionPurchaseActiveInterval(
      "2012-09-30T12:24:36.000Z", "2012-11-06T13:24:36.000Z"))), 0)

  def successValidatedTransaction: ValidatedTransaction = ValidatedTransaction(
    "20000034829192",
    1,
    1,
    Some(ValidatedTransactionPurchase(
      "uk.co.guardian.gla.1month",
      "1000000036022769",
      ValidatedTransactionPurchaseActiveInterval("2018-03-26T12:24:23.107Z", "2018-03-26T12:29:23.107Z"))),

    0
  )
}

case class ValidateExample(requestString: String, responseString: String)
